export default {
    name:'submit',
    data:function(){
        return{
            contents:'',
            firsttime:'',
            phone:'',
        }
    },
    created(){
        document.title = 'submit的title'
    },
    mounted:function(){
        laydate.render({
            elem: '#time', //指定元素
            type: 'month'
        })
    },
    methods:{
        save() {
            var name = $("#name").val().trim();
            var sex = $("#sex").val().trim();
            var idcard = $("#idcard").val().trim();
            var idaddress = $("#idcardaddr").val().trim();
            var phone = $("#phone").val().trim();
            var platloginid = $("#compony").val().trim();
            var firsttime = $("#time").val().trim();
            var suminvestment = $("#totalmoney").val().trim();
            var getinvestment = $("#returnmoney").val().trim();
            var unInvestment = $("#nomoney").val().trim();
            var setplatform = $("#remitbank").val().trim();
            var setaccount = $("#remitaccount").val().trim();
            var getplatform = $("#getaccount").val().trim();
            var getaccount = $("#returaccount").val().trim();
            var contents = $("#other").val().trim();
            if (name == "") {
                layer.msg("姓名不能为空");
                return;
            }
            if (sex == "") {
                layer.msg("请输入性别");
                return;
            }
            if (idcard == "") {
                layer.msg("身份证号码不能为空");
                return;
            }
            if (idaddress == "") {
                layer.msg("身份证地址不能为空");
                return;
            }
            if (phone.length != 11) {
                layer.msg("请输入11位正确的手机号码");
                return;
            }
            else if (!(/^1(3|4|5|6|7|8|9)\d{9}$/.test(phone))) {
                layer.msg("请输入11位正确的手机号码");
                return;
            }
            if (platloginid == "") {
                layer.msg("平台登录账号不能为空");
                return;
            }
            if (firsttime == "") {
                layer.msg("首次投资平台时间不能为空");
                return;
            }
            if (suminvestment == "") {
                layer.msg("总投资额不能为空");
                return;
            }
            if (getinvestment == "") {
                layer.msg("已收回款不能为空");
                return;
            }
            if (unInvestment == "") {
                layer.msg("未收回款不能为空");
                return;
            }
            if (setplatform == "") {
                layer.msg("汇款银行/平台不能为空");
                return;
            }
            if (setaccount == "") {
                layer.msg("汇款账号不能为空");
                return;
            }
            if (getplatform == "") {
                // layer.close(index);
                layer.msg("收款银行/平台不能为空");
                return;
            }
            if (getaccount == "") {
                layer.msg("收款账号不能为空");
                return;
            }
            var isadd = 0;
            if (isadd == 0) {
                var index = layer.load(1, {
                    shade: [0.5, '#000'] //0.1透明度的白色背景
                });
                isadd = 1;
                $.ajax({
                    url: '/Information/Add',
                    data: {
                        "Name": name,
                        "Sex": sex,
                        "IdCard": idcard,
                        "IdAddress": idaddress,
                        "Phone": phone,
                        "PlatLoginId": platloginid,
                        "FirstTime": firsttime,
                        "SumInvestment": suminvestment,
                        "GetInvestment": getinvestment,
                        "UnInvestment": unInvestment,
                        "SetPlatform": setplatform,
                        "SetAccount": setaccount,
                        "GetPlatform": getplatform,
                        "GetAccount": getaccount,
                        "Contents": contents,
                        "Type":'17'
                    },
                    type: 'post',
                    success: function (xml) {
                        if (xml == "0") {
                            isadd = 0;
                            $(":input").val("");
                            $('.success-lay').removeClass('hide');
                            $('.wraper').css("overflow", "hidden");
                            layer.close(index);
                        } if (xml == "1") {
                            isadd = 0;
                            layer.msg("操作失败,请稍后再试");
                            layer.close(index);
                            return;
                        }
                    }
                })
            } else {
                layer.msg("请勿重复提交信息");
            }
        },
        checkPhone(){
            if (!(/^1(3|4|5|6|7|8|9)\d{9}$/.test(this.phone))) {
                layer.msg("请输入11位正确的手机号码");
                return;
            }
        }
    },
    watch:{
        //监视变量内容的值变化了就触发
        contents:function (){
            var l = this.contents.length;
            $('.span-num').text(l);
        },
    }

}
